import {StyleSheet} from 'react-native';

const styles = StyleSheet.create({
    headingContainer: {
        display: 'flex',
        textAlign: "center",
    },

    header: {
        backgroundColor: 'rgb(92, 92, 92)',
        padding: '1.5%',
        fontSize: '24px',
        marginTop: '15px',
    },

    spaces: {
        display: "flex",
        // flexWrap: "wrap",
        flexDirection: "column",
        padding: '5%',
    },

    buttons: {
        backgroundColor: 'grey',
        fontSize: '16px',
        padding: '20%',
        borderRadius: '30px',
        textAlign: "center",
        width: "40%",
        backgroundColor: 'rgb(92, 92, 92)',

    },

});

export default styles;
